charts.query:
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>
PREFIX schema: <https://schema.org/>
SELECT ?label (COUNT(?item) AS ?count)
WHERE {
  GRAPH ?g {
    ?item a schema:Dataset .
    OPTIONAL {
      ?item schema:additionalType ?additionalType .
      ?additionalType rdfs:label ?label .
    }
    BIND(COALESCE(?label, "Other") AS ?label)
  }
}
GROUP BY ?label
ORDER BY DESC(?count)

PREFIX skos: <http://www.w3.org/2004/02/skos/core#>
SELECT ?label (COUNT(?label) AS ?count) WHERE { 
 {
 SELECT (REPLACE(STR(?rawLabel), " - TADIRAH$", "") AS ?label) WHERE { 
 ?s <https://w3id.org/dh-atlas/methodology> ?o.
 ?o skos:prefLabel ?rawLabel .
 }
 }
} 
GROUP BY ?label
ORDER BY DESC(?count)
LIMIT 8

PREFIX skos: <http://www.w3.org/2004/02/skos/core#>
SELECT ?label (COUNT(?label) AS ?count) WHERE { 
 {
 SELECT (REPLACE(STR(?rawLabel), " - DATASET STATUS$", "") AS ?label) WHERE { 
 ?s <https://schema.org/creativeWorkStatus> ?o.
 ?o skos:prefLabel ?rawLabel .
 }
 }
} 
GROUP BY ?label

PREFIX schema: <https://schema.org/>
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>

SELECT ?classLabel ?entity ?label ?count
WHERE {
  {
    SELECT ?classLabel ?entity ?label (COUNT(?entity) AS ?count)
    WHERE {
      GRAPH ?g1 { ?g1 ?p ?g2 . }
      GRAPH ?g2 {
        ?entity a schema:Person .
        OPTIONAL { ?entity rdfs:label ?label }
      }
      BIND("Person" AS ?classLabel)
    }
    GROUP BY ?entity ?label ?classLabel
    ORDER BY DESC(?count)
    LIMIT15
  }
  UNION
  {
    SELECT ?classLabel ?entity ?label (COUNT(?entity) AS ?count)
    WHERE {
      GRAPH ?g1 { ?g1 ?p ?g2 . }
      GRAPH ?g2 {
        ?entity a schema:Place .
        OPTIONAL { ?entity rdfs:label ?label }
      }
      BIND("Place" AS ?classLabel)
    }
    GROUP BY ?entity ?label ?classLabel
    ORDER BY DESC(?count)
    LIMIT15
  }
  UNION
  {
    SELECT ?classLabel ?entity ?label (COUNT(?entity) AS ?count)
    WHERE {
      GRAPH ?g1 { ?g1 ?p ?g2 . }
      GRAPH ?g2 {
        ?entity a schema:Thing .
        OPTIONAL { ?entity rdfs:label ?label }
      }
      BIND("Thing" AS ?classLabel)
    }
    GROUP BY ?entity ?label ?classLabel
    ORDER BY DESC(?count)
    LIMIT15
  }
  UNION
  {
    SELECT ?classLabel ?entity ?label (COUNT(?entity) AS ?count)
    WHERE {
      GRAPH ?g1 { ?g1 ?p ?g2 . }
      GRAPH ?g2 {
        ?entity a schema:CreativeWork .
        OPTIONAL { ?entity rdfs:label ?label }
      }
      BIND("Creative Work" AS ?classLabel)
    }
    GROUP BY ?entity ?label ?classLabel
    ORDER BY DESC(?count)
    LIMIT15
  }
  UNION
  {
    SELECT ?classLabel ?entity ?label (COUNT(?entity) AS ?count)
    WHERE {
      GRAPH ?g1 { ?g1 ?p ?g2 . }
      GRAPH ?g2 {
        ?entity a schema:Time .
        OPTIONAL { ?entity rdfs:label ?label }
      }
      BIND("Time" AS ?classLabel)
    }
    GROUP BY ?entity ?label ?classLabel
    ORDER BY DESC(?count)
    LIMIT15
  }
}
ORDER BY ?classLabel DESC(?count)
